import {
  Box,
  Center,
  Heading,
  Text,
  Stack,
  Avatar,
  useColorModeValue,
  Image,
  Badge,
} from '@chakra-ui/react'

import { LinkBox, LinkOverlay } from '@chakra-ui/react'

export default function AspectCardMariela() {
  return (
    <Center py={6}>
      <LinkBox>
        <Box
          maxW={'445px'}
          w={'full'}
          bg={useColorModeValue('white', 'gray.900')}
          boxShadow={'2xl'}
          rounded={'md'}
          p={6}
          overflow={'hidden'}
        >
          <LinkOverlay href='#'>
            <Box
              h={'210px'}
              bg={'gray.100'}
              mt={-6}
              mx={-6}
              mb={-9}
              pos={'relative'}
            >
              <Image
                transform='scale(1.0)'
                src={
                  'https://www.onehistoryprimary.org/wp-content/uploads/2018/11/Codexplatecrop.jpg'
                }
                alt='maya agriculture'
                height='fill'
                objectFit='contain'
                transition='0.3s ease-in-out'
                width={'100%'}
                _hover={{
                  transform: 'scale(1.05)',
                }}
              />
            </Box>
          </LinkOverlay>
          <Stack mt={36}>
            <Text
              color={'primary'}
              textTransform={'uppercase'}
              fontWeight={800}
              fontSize={'sm'}
              letterSpacing={1.1}
            >
              Teaching Tool
            </Text>
            <Stack direction='row'>
              <Badge
                borderRadius='full'
                px='2'
                colorScheme='yellow'
                variant='subtle'
              >
                Agriculture
              </Badge>
              <Badge borderRadius='full' px='2' colorScheme='red'>
                Warfare
              </Badge>
            </Stack>
            <Heading
              color={useColorModeValue('gray.700', 'white')}
              fontSize={'2xl'}
              fontFamily={'body'}
              isTruncated
            >
              Mayan Agriculture and Warfare
            </Heading>
            <Text color={'gray.500'}>
              Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam
              nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam
              erat, sed diam voluptua. At vero eos et accusam et justo duo
              dolores et ea rebum.
            </Text>
          </Stack>
          <Stack mt={6} direction={'row'} spacing={4} align={'center'}>
            <Avatar
              src={''}
              alt={'Author'}
              name='Mariela Aranda'
              bg={'orange.400'}
            />
            <Stack direction={'column'} spacing={0} fontSize={'sm'}>
              <Text fontWeight={600}>Mariela</Text>
              <Text color={'gray.500'}>Oct 10, 2021 Â· 6min read</Text>
            </Stack>
          </Stack>
        </Box>
      </LinkBox>
    </Center>
  )
}
